type Class int

const (
	Invalid         Class = -1
	Blank           Class = 0
	Punctation      Class = 1
	Word            Class = 2
	Emoji           Class = 3
	SuperScript     Class = 0x2070
	SubScript       Class = 0x2080
	Braille         Class = 0x2800
	Hiragana        Class = 0x3040
	Katakana        Class = 0x30a0
	CJKIdeographs   Class = 0x4e00
	HungulSyllables Class = 0xac00
)

