func Is(r rune) Class {
	if r < 0x100 {
		if r == ' ' || r == '\t' || r == 0 || r == '\r' || r == '\n' {
			return 0 // blank
		}
		if !unicode.IsPunct(r) {
			return 2 // word
		}
		return 1 // punctation
	}

	for _, clazz := range classes {
		if clazz.first < r && r < clazz.last {
			return Class(clazz.value)
		}
	}
	for _, v := range emoji {
		if v.first < r && r < v.last {
			return 3 // emoji
		}
	}
	return 2 // word
}

func Split(s string) []string {
	scan := bufio.NewScanner(strings.NewReader(s))
	scan.Split(SplitClass)
	var words []string
	for scan.Scan() {
		words = append(words, scan.Text())
	}
	return words
}

func SplitClass(data []byte, atEOF bool) (int, []byte, error) {
	if atEOF && len(data) == 0 {
		return 0, nil, nil
	}
	bpos := 0
	b := data
	last := Invalid
	for {
		r, i := utf8.DecodeRune(b)
		if i == 0 {
			break
		}
		clazz := Is(r)
		if last == -1 {
			last = clazz
		} else if clazz != last {
			last = clazz
			break
		}
		bpos += i
		b = b[i:]
	}
	if !atEOF && !utf8.FullRune(b) {
		return 0, nil, nil
	}
	return bpos, data[:bpos], nil
}
